import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './Header';

const Profile = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = () => {
    const userEmail = localStorage.getItem('user');
    if (userEmail) {
        const email = JSON.parse(userEmail).email;
        axios.get(`http://localhost:5000/api/getUser?email=${email}`)
            .then(response => {
                console.log(response.data);
                setUser(response.data);
            })
            .catch(error => {
                console.error('Error fetching user data:', error);
            });
    }
};


  return (
    <div>
      <Header />
      {user && (
        <div>
          <h2>Profil korisnika</h2>
          <p>Ime: {user.FirstName}</p>
          <p>Prezime: {user.LastName}</p>
          <p>Email: {user.Email}</p>
          <p>Adresa: {user.Address}</p>
          <p>Grad: {user.City}</p>
          <p>Dr≈æava: {user.Country}</p>
          <p>Broj telefona: {user.PhoneNumber}</p>
          <p>Slika profila: {user.ProfilePicture}</p>
        </div>
      )}
    </div>
  );
};

export default Profile;
