import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './Header';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    city: '',
    country: '',
    phoneNumber: '',
    password: '',
    profilePicture: ''
  });

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = () => {
    const userEmail = localStorage.getItem('user');
    if (userEmail) {
      const email = JSON.parse(userEmail).email;
      axios.get(`http://localhost:5000/api/getUser/${email}`)
        .then(response => {
          setUser(response.data);
          setFormData({
            firstName: response.data.firstName,
            lastName: response.data.lastName,
            address: response.data.address,
            city: response.data.city,
            country: response.data.country,
            phoneNumber: response.data.phoneNumber,
            password: response.data.password
          });
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    }
  };

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const response = await axios.put('http://localhost:5000/api/editProfile', formData);
      console.log(response.data);
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <div>
      <Header />
      {user && (
        <div>
          <h2>Profil korisnika</h2>
          <form onSubmit={handleSubmit}>
            <label>
              Ime:
              <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />
            </label>
            <button type="submit">Saƒçuvaj izmene</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Profile;
