import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './Header';
import { Container, Table, Button } from 'react-bootstrap';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [editable, setEditable] = useState(false); // Da li je profil editable
  const [editedUser, setEditedUser] = useState(null); // Za čuvanje izmenjenih podataka

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = () => {
    const userEmail = localStorage.getItem('user');
    if (userEmail) {
      const email = JSON.parse(userEmail).email;
      axios.get(`http://localhost:5000/api/getUser?email=${email}`)
        .then(response => {
          setUser(response.data);
          setEditedUser(response.data); // Postavljamo izmenjenog korisnika na početni profil
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    }
  };

  const handleEdit = () => {
    setEditable(true);
  };

  const handleSaveChanges = () => {
    // Implementirajte logiku za čuvanje izmenjenih podataka, npr. slanjem HTTP zahteva
    setEditable(false);
    setUser(editedUser); // Postavljamo izmenjenog korisnika kao trenutnog korisnika
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedUser(prevUser => ({
      ...prevUser,
      [name]: value
    }));
  };

  return (
    <div>
      <Header />
      {user && (
        <Container className="mt-5">
          <h2 className="text-center mb-4">Profil korisnika</h2>
          <Table striped bordered hover className="text-center">
            <tbody>
              <tr>
                <td>Ime:</td>
                <td>{editable ? <input type="text" name="FirstName" value={editedUser.FirstName} onChange={handleChange} className="form-control" /> : user.FirstName}</td>
              </tr>
              <tr>
                <td>Prezime:</td>
                <td>{editable ? <input type="text" name="LastName" value={editedUser.LastName} onChange={handleChange} className="form-control" /> : user.LastName}</td>
              </tr>
              <tr>
                <td>Email:</td>
                <td>{editable ? <input type="text" name="Email" value={editedUser.Email} onChange={handleChange} className="form-control" /> : user.Email}</td>
              </tr>
              <tr>
                <td>Adresa:</td>
                <td>{editable ? <input type="text" name="Address" value={editedUser.Address} onChange={handleChange} className="form-control" /> : user.Address}</td>
              </tr>
              <tr>
                <td>Grad:</td>
                <td>{editable ? <input type="text" name="City" value={editedUser.City} onChange={handleChange} className="form-control" /> : user.City}</td>
              </tr>
              <tr>
                <td>Država:</td>
                <td>{editable ? <input type="text" name="Country" value={editedUser.Country} onChange={handleChange} className="form-control" /> : user.Country}</td>
              </tr>
              <tr>
                <td>Broj telefona:</td>
                <td>{editable ? <input type="text" name="PhoneNumber" value={editedUser.PhoneNumber} onChange={handleChange} className="form-control" /> : user.PhoneNumber}</td>
              </tr>
              <tr>
                <td>Slika profila:</td>
                <td>{editable ? <input type="text" name="ProfilePicture" value={editedUser.ProfilePicture} onChange={handleChange} className="form-control" /> : user.ProfilePicture}</td>
              </tr>
            </tbody>
          </Table>
          <div className="text-center">
            {!editable && <Button variant="primary" onClick={handleEdit}>Edit</Button>}
            {editable && <Button variant="success" onClick={handleSaveChanges}>Save Changes</Button>}
          </div>
        </Container>
      )}
    </div>
  );
};

export default Profile;
