import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/PortfolioPage.css';
import Header from './Header';

const PortfolioPage = () => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
    const [cryptos, setCryptos] = useState([]);
    const [cryptoSymbol, setCryptoSymbol] = useState('');
    const [cryptoName, setCryptoName] = useState('');
    const [cryptoPrice, setCryptoPrice] = useState(0);
    const [cryptoAmount, setCryptoAmount] = useState(0);
    const [cryptoOptions, setCryptoOptions] = useState([]);

    const symbolToName = {
        BTC: 'Bitcoin', ETH: 'Ethereum', USDT: 'Tether', BNB: 'Binance Coin', SOL: 'Solana', STETH: 'Staked Ether', USDC: 'USD Coin', XRP: 'XRP', DOGE: 'Dogecoin', TON: 'TON Crystal', ADA: 'Cardano', AVAX: 'Avalanche', SHIB: 'Shiba Inu', WBTC: 'Wrapped Bitcoin', TRX: 'TRON', DOT: 'Polkadot', BCH: 'Bitcoin Cash', LINK: 'Chainlink', NEAR: 'Near Protocol', UNI: 'Uniswap'
      };

      useEffect(() => {
        if (user) {
          getAllCryptos(user.email);
          calculateTotalPortfolioValue(cryptos).then(totalValue => {
            console.log('Ukupna vrednost portfolija:', totalValue);
          });
        }
      }, [user, cryptos]);
      
      

      useEffect(() => {
        if (cryptoSymbol) {
          getCryptoPrice(cryptoSymbol);
        }
      }, [cryptoSymbol]);

    useEffect(() => {
      if (user) {
        getAllCryptos(user.email);
        getTopCryptoSymbols();
      }
    }, [user]);
  
    const getAllCryptos = async (userEmail) => {
      try {
        const response = await axios.get(`http://localhost:5000/api/crypto?userEmail=${userEmail}`);
        setCryptos(response.data);
      } catch (error) {
        console.error('Error fetching cryptos:', error);
      }
    };

    const handleSellCrypto = async () => {
        try {
          const existingCrypto = cryptos.find(c => c.Symbol === cryptoSymbol);
      
          if (existingCrypto) {
            await axios.post(`http://localhost:5000/api/crypto/sell`, {
              userEmail: user.email,
              symbol: cryptoSymbol,
              amount: cryptoAmount,
              price: cryptoPrice
            });
          } else {
            console.error('Kriptovaluta koju pokušavate da prodate nije pronađena.');
          }
          getAllCryptos(user.email);
        } catch (error) {
          console.error('Greška prilikom prodaje kriptovalute:', error);
        }
      };      
  
      const getCurrentPricesForPortfolio = async (cryptos) => {
        try {
          const symbols = cryptos.map(crypto => crypto.Symbol).join(',');
          const response = await axios.get(`https://api.coingecko.com/api/v3/simple/price?ids=${symbols}&vs_currencies=usd`);
          return response.data;
        } catch (error) {
          console.error('Greška prilikom dobavljanja trenutnih cena:', error);
          return null;
        }
      };
      
      const calculateTotalPortfolioValue = async (cryptos) => {
        const currentPrices = await getCurrentPricesForPortfolio(cryptos);
        if (!currentPrices) return 0;
      
        let totalValue = 0;
        cryptos.forEach(crypto => {
          const price = currentPrices[crypto.Symbol]?.usd;
          if (price) {
            totalValue += crypto.Amount * price;
          }
        });
        return totalValue;
      };
      

    const getTopCryptoSymbols = async () => {
      try {
        const response = await axios.get('https://api.coingecko.com/api/v3/coins/markets', {
          params: {
            vs_currency: 'usd',
            per_page: 20,
            page: 1,
            order: 'market_cap_desc',
            sparkline: false
          }
        });
        const symbols = response.data.map(crypto => crypto.symbol);
        setCryptoOptions(symbols);
      } catch (error) {
        console.error('Error fetching top crypto symbols:', error);
      }
    };
  
    const getCryptoPrice = async (symbol) => {
        try {
          const response = await axios.get(`https://api.coingecko.com/api/v3/simple/price?ids=${symbol}&vs_currencies=usd`);
          const price = response.data[symbol]?.usd;
          setCryptoPrice(price);
          setCryptoName(symbolToName[symbol] || symbol.toUpperCase());
        } catch (error) {
          console.error(`Error fetching price for ${symbol}:`, error);
          setCryptoName('');
        }
    };      
  
    const handleAddOrBuyCrypto = async () => {
      try {
        const existingCrypto = cryptos.find(c => c.Symbol === cryptoSymbol);
  
        if (existingCrypto) {
          await axios.post(`http://localhost:5000/api/crypto/buy`, {
            userEmail: user.email,
            symbol: cryptoSymbol,
            amount: cryptoAmount,
            price: cryptoPrice
          });
        } else {
          await axios.post(`http://localhost:5000/api/crypto`, {
            userEmail: user.email,
            name: cryptoName,
            currentPrice: cryptoPrice,
            symbol: cryptoSymbol,
            amount: cryptoAmount
          });
        }
        getAllCryptos(user.email);
      } catch (error) {
        console.error('Error adding or buying crypto:', error);
      }
    };
  
    const handleLogout = () => {
      localStorage.removeItem('user');
      setUser(null);
    };
  
    if (!user) {
      return <p>Niste ulogovani.</p>;
    }
  
    return (
      <div style={{ textAlign: 'center' }}>
        <Header />
        <h2>Dobrodošli, {user.email}</h2>
        <button onClick={handleLogout}>Logout</button>
        <p>Ovo je vaša portfolio stranica.</p>
        <h3>Vaše kriptovalute:</h3>
        <ul style={{ textAlign: 'left' }}>
          {cryptos.map(crypto => (
            <li key={crypto.RowKey}>
              {crypto.Name} ({crypto.Symbol}) - {crypto.Amount} - {crypto.CurrentPrice}
            </li>
          ))}
        </ul>
        <form className="crypto-form">
          <h3>Unesite informacije o kriptovaluti:</h3>
          <label>
            Symbol:
            <select value={cryptoSymbol} onChange={e => setCryptoSymbol(e.target.value)}>
              <option value="">Izaberite simbol</option>
              {cryptoOptions.map(symbol => (
                <option key={symbol} value={symbol}>{symbol.toUpperCase()}</option>
              ))}
            </select>
          </label>
          <br />
          <label>
            Ime:
            <input
              type="text"
              value={cryptoName}
              onChange={e => setCryptoName(e.target.value)}
            />
          </label>
          <br />
          <label>
            Cena:
            <input
              type="number"
              value={cryptoPrice}
              onChange={e => setCryptoPrice(parseFloat(e.target.value))}
            />
          </label>
          <br />
          <label>
            Količina:
            <input
              type="number"
              value={cryptoAmount}
              onChange={e => setCryptoAmount(parseFloat(e.target.value))}
            />
          </label>
          <br />
          <button type="button" onClick={handleAddOrBuyCrypto}>Buy</button>
          <button type="button" onClick={handleSellCrypto}>Prodaj</button>
        </form>
      </div>
    );
  };
  
  export default PortfolioPage;
