import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './Header';

const Profile = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = () => {
    const userEmail = localStorage.getItem('user');
    if (userEmail) {
      const email = JSON.parse(userEmail).email;
      axios.get(`http://localhost:5000/api/getUser`)
        .then(response => {
          setUser(response.data);
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    }
  };

  return (
    <div>
      <Header />
      {user && (
        <div>
          <h2>Profil korisnika</h2>
          <p>Ime: {user.firstName}</p>
          <p>Prezime: {user.lastName}</p>
          <p>Email: {user.email}</p>
          <p>Adresa: {user.address}</p>
          <p>Grad: {user.city}</p>
          <p>Dr≈æava: {user.country}</p>
          <p>Broj telefona: {user.phoneNumber}</p>
          <p>Slika profila: {user.profilePicture}</p>
        </div>
      )}
    </div>
  );
};

export default Profile;
