import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './Header';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    city: '',
    country: '',
    phoneNumber: '',
    password: '',
    profilePicture: ''
  });

  useEffect(() => {
    const userEmail = localStorage.getItem('user');
    const email = JSON.parse(userEmail).email;
    console.log(userEmail);
    if (userEmail) {
      axios.get(`http://localhost:5000/api/getUser/${email}`)
        .then(response => {
          setUser(response.data);

            console.log('firstname:', response.data.firstName);
            console.log('lastName:', response.data.lastName);
            console.log('address:', response.data.address);

          setFormData({
            firstName: response.data[0].firstName,
            lastName: response.data[0].lastName, // Promenjeno response.data u response.data[0]
            address: response.data[0].address, // Promenjeno response.data u response.data[0]
            city: response.data[0].city, // Promenjeno response.data u response.data[0]
            country: response.data[0].country, // Promenjeno response.data u response.data[0]
            phoneNumber: response.data[0].phoneNumber, // Promenjeno response.data u response.data[0]
            password: response.data[0].password, // Promenjeno response.data u response.data[0]
            profilePicture: response.data[0].profilePicture // Promenjeno response.data u response.data[0]
          });

          console.log('firstname:', response.data.firstName);
          console.log('lastName:', response.data.lastName);
          console.log('address:', response.data.address);

        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    }
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const response = await axios.put('http://localhost:5000/api/editProfile', formData);
      console.log(response.data);
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <div>
      <Header />

      <h2>Profil korisnika</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Ime:
          <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />
        </label>
        <button type="submit">Saƒçuvaj izmene</button>
      </form>
    </div>
  );
};

export default Profile;
