import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './Header';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [editable, setEditable] = useState(false); // Da li je profil editable
  const [editedUser, setEditedUser] = useState(null); // Za čuvanje izmenjenih podataka

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = () => {
    const userEmail = localStorage.getItem('user');
    if (userEmail) {
      const email = JSON.parse(userEmail).email;
      axios.get(`http://localhost:5000/api/getUser?email=${email}`)
        .then(response => {
          setUser(response.data);
          setEditedUser(response.data); // Postavljamo izmenjenog korisnika na početni profil
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    }
  };

  const handleEdit = () => {
    setEditable(true);
  };

  const handleSaveChanges = () => {
    // Implementirajte logiku za čuvanje izmenjenih podataka, npr. slanjem HTTP zahteva
    setEditable(false);
    setUser(editedUser); // Postavljamo izmenjenog korisnika kao trenutnog korisnika
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedUser(prevUser => ({
      ...prevUser,
      [name]: value
    }));
  };

  return (
    <div>
      <Header />
      {user && (
        <div>
          <h2>Profil korisnika</h2>
          <table className="profile-table">
            <tbody>
              <tr>
                <td>Ime:</td>
                <td>{editable ? <input type="text" name="FirstName" value={editedUser.FirstName} onChange={handleChange} /> : user.FirstName}</td>
              </tr>
              <tr>
                <td>Prezime:</td>
                <td>{editable ? <input type="text" name="LastName" value={editedUser.LastName} onChange={handleChange} /> : user.LastName}</td>
              </tr>
              <tr>
                <td>Email:</td>
                <td>{editable ? <input type="text" name="Email" value={editedUser.Email} onChange={handleChange} /> : user.Email}</td>
              </tr>
              <tr>
                <td>Adresa:</td>
                <td>{editable ? <input type="text" name="Address" value={editedUser.Address} onChange={handleChange} /> : user.Address}</td>
              </tr>
              <tr>
                <td>Grad:</td>
                <td>{editable ? <input type="text" name="City" value={editedUser.City} onChange={handleChange} /> : user.City}</td>
              </tr>
              <tr>
                <td>Država:</td>
                <td>{editable ? <input type="text" name="Country" value={editedUser.Country} onChange={handleChange} /> : user.Country}</td>
              </tr>
              <tr>
                <td>Broj telefona:</td>
                <td>{editable ? <input type="text" name="PhoneNumber" value={editedUser.PhoneNumber} onChange={handleChange} /> : user.PhoneNumber}</td>
              </tr>
              <tr>
                <td>Slika profila:</td>
                <td>{editable ? <input type="text" name="ProfilePicture" value={editedUser.ProfilePicture} onChange={handleChange} /> : user.ProfilePicture}</td>
              </tr>
            </tbody>
          </table>
          <div>
            {!editable && <button onClick={handleEdit}>Edit</button>}
            {editable && <button onClick={handleSaveChanges}>Save Changes</button>}
          </div>
        </div>
      )}
    </div>
  );
};

export default Profile;
